<script>

$(function() {

 var name = $( "#name" ),
      email = $( "#email" ),
      password = $( "#password" ),
      allFields = $( [] ).add( name ).add( email ).add( password ),
      tips = $( ".validateTips" );

    $( "#dialog-form" ).dialog({
      autoOpen: false,
      height: 370,
      width: 350,
      modal: true,
      buttons: {
        "Next": function() {
          var bValid = false;
	   
          //allFields.removeClass( "ui-state-error" );
 	    bValid = $("#pincodeHidden").val().length == 6;
          if ( bValid ) {
            $('#pincodeForm').unbind('submit').submit();
            $( this ).dialog( "close" );
          }else
	    alert("Sorry, currently we do not provide service to the area/pincode entered by you.");
        },
        Cancel: function() {
          $( this ).dialog( "close" );
        }
      },
      close: function() {
        allFields.val( "" ).removeClass( "ui-state-error" );
      }
    });


var availableCode = new Array();

$.ajax({
    url:"services/pincodeData.php",
    type:"GET",
    dataType : "json",
    success:function(msg){

      availableCode = msg;

 
 $( "#pincode" ).autocomplete({
      source: function(request, response) {
        var results = $.ui.autocomplete.filter(availableCode , request.term);

        response(results.slice(0, 5));
    },
 	response: function( event, ui ) {
	if(ui.content.length <= 0)
	{
	$("#subDetails").css("display","none");	
	$("#pincodeHidden").val("");
	$("#areaHidden").val("");
	}else if(ui.content.length ==1)
	   fetchAmount(ui.content[0].label);	
}
     ,
      select: function( event, ui ) {
	fetchAmount(ui.item.label);
      },
      minLength: 4
    });


    }
});


$("#pincodeForm").submit( function(e) {
e.preventDefault();
});
 
});

function fetchAmount(value)
{

var h = value.split("-");
var pincode = h[0];
var area = h[1];
var refund = 500;
var reg = 100;
var total = 0;

$.ajax({
  type: "POST",
  url: "services/pincodeAmnt.php",
  data: { pincode: pincode }
})
  .done(function( msg ) {
var rate = msg;
$("#subDetails").css("display","block");
$("#pinspan").text(pincode);
$("#pincodeHidden").val(pincode);
$("#areaHidden").val(area);
$("#areaspan").text(area);
$("#subspan").text(rate);
$("#regspan").text(reg);
$("#refundspan").text(refund);
total = parseInt(rate) + parseInt(refund) + parseInt(reg);
$("#totalspan").text(total);
});

}

</script>

